<?phpget_header();?><?php // Featured imageif ( has_post_thumbnail() ) { 	$thumb = wp_get_attachment_image_src( get_post_thumbnail_id($post->ID), 'full');?>	<figure class="page-image" style="background-image: url('<?php echo $thumb['0'];?>');">		<h1><?php the_title(); ?></h1>	</figure><?php } ?><section class="section-page">	<div class="container">		<article class="col-sm-10 col-xs-12">			<?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?>								<?php // Show title if no featured image				if(!has_post_thumbnail()) { ?>					<h1><?php the_title(); ?></h1>				<?php } ?>				<?php the_content(); ?>							<?php endwhile; endif; ?>		</article>	</div></section><?phpfunction is_tree($pid) {	global $post;	if(is_page()&&($post->post_parent==$pid||is_page($pid)))        return true;	else        return false;}; ?><?php if (is_tree(6)) {get_template_part('tjanster');} ?><?php get_footer(); ?>